IMPLtoLogs=Join[Table[IMPL[0,PadLeft[{},i],y_]->Log[y]^i/(i!),{i,10}],Table[IMPL[0,PadLeft[{},i,1],1-y_]->-(-Log[y])^i/(i!),{i,10}],Table[IMPL[0,PadRight[{1},i+1],y_]->-PolyLog[i+1,y],{i,0,9}]];
